<?php$count=0;$getShowID=getShowID(mysql_real_escape_string($_GET['q']),1);if($getShowID){foreach($getShowID as $getShow){	$count++;	$showurl='?show='.str_replace (' ','-', $getShow['showname']).'&showid='.$getShow['showid'];	echo '<a title="See all Episodes from '. $getShow['showname'].'" ';	echo 'href="'.$showurl.'">'.$getShow['showname'].'</a> <br>';}}else{	$getEpisodes=getEpisodes4("where `title` like '%".mysql_real_escape_string($_GET['q'])."%' limit 10");	if($getEpisodes){foreach($getEpisodes as $getEpisode){		include('list.php');		$count++;	}}}if($count==0){	echo '<gcse:searchresults-only></gcse:searchresults-only> ';}	if($count==1){	echo 'Loading...<meta http-equiv="Refresh" content="0;URL='.$showurl.'">';}	